library(ggplot2)
data <- read.csv("nirA.csv", header = T, check.names = F)
data$phylum <- factor(data$phylum,levels = c("Proteobacteria","Acidobacteriota","SAR324","Desulfobacterota_B","Chloroflexota","Planctomycetota","Thermoproteota","Nitrospirota","Methylomirabilota","Desulfobacterota_D","Myxococcota_A","Verrucomicrobiota","Bacteroidota","Nitrospinota","Poribacteria"))
mycol <- c("#d4a6c8","#4e79a7","#ffbe7d","#fabfd2","#e15759","#f1ce63","#00a98f","#86bcb6","#ff9d9a","#a0cbe8","#b07aa1","#bab0ac","#8cd17d","#f28e2b","#79706e")
p <- ggplot(data, aes(x=gene, y=num, fill=phylum)) + geom_bar(stat = "identity",position ="fill") + scale_fill_manual(values=mycol) + labs(x = "Sample" , y = "Relative abundance(%)") + theme(axis.title=element_text(size=15),axis.text.x = element_text(angle=45, hjust = 1, vjust = 1 ,size = 12),axis.text.y = element_text(size = 12),axis.ticks.length=unit(0.15,"cm") ,panel.background = element_blank(),axis.line = element_line(color = "black"))
ggsave("nirA.pdf",p,width = 8,height = 8)